'use strict';

/**
 * One-off readable stream
 */ function createReadableStream() {
    let controller = null;
    let cancelled = false;
    const readable = new ReadableStream({
        start (c) {
            controller = c;
        },
        cancel () {
            cancelled = true;
        }
    });
    return {
        readable,
        controller,
        cancelled () {
            return cancelled;
        }
    };
}

exports.createReadableStream = createReadableStream;
